contracts/VotingSystem.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/security/ReentrancyGuard.sol";

contract VotingSystem is Ownable, ReentrancyGuard {
    struct Vote {
        bool isUpvote;
        uint256 timestamp;
    }

    struct Article {
        uint256 upvotes;
        uint256 downvotes;
        uint256 lastVoteTimestamp;
        uint256 votesInTimeWindow;
        bool isFrozen;
        mapping(address => Vote) userVotes;
    }

    // Constants
    uint256 public constant TIME_WINDOW = 2 minutes;
    uint256 public constant MAX_VOTES_IN_WINDOW = 5;
    
    // State variables
    mapping(uint256 => Article) private articles;
    
    // Events
    event VoteCast(uint256 indexed articleId, address indexed voter, bool isUpvote);
    event ArticleFrozen(uint256 indexed articleId, uint256 timestamp);
    event ArticleUnfrozen(uint256 indexed articleId, uint256 timestamp);

    // Modifiers
    modifier articleNotFrozen(uint256 _articleId) {
        require(!articles[_articleId].isFrozen, "Article voting is frozen");
        _;
    }

    modifier hasNotVoted(uint256 _articleId) {
        require(articles[_articleId].userVotes[msg.sender].timestamp == 0, "Already voted");
        _;
    }

    function vote(uint256 _articleId, bool _isUpvote) external nonReentrant articleNotFrozen(_articleId) hasNotVoted(_articleId) {
        Article storage article = articles[_articleId];
        
        // Check and update votes in time window
        if (block.timestamp - article.lastVoteTimestamp > TIME_WINDOW) {
            article.votesInTimeWindow = 1;
        } else {
            article.votesInTimeWindow++;
            if (article.votesInTimeWindow > MAX_VOTES_IN_WINDOW) {
                article.isFrozen = true;
                emit ArticleFrozen(_articleId, block.timestamp);
                return;
            }
        }

        // Record vote
        if (_isUpvote) {
            article.upvotes++;
        } else {
            article.downvotes++;
        }

        // Record user vote
        article.userVotes[msg.sender] = Vote({
            isUpvote: _isUpvote,
            timestamp: block.timestamp
        });

        article.lastVoteTimestamp = block.timestamp;
        
        emit VoteCast(_articleId, msg.sender, _isUpvote);
    }

    function unfreezeArticle(uint256 _articleId) external onlyOwner {
        require(articles[_articleId].isFrozen, "Article is not frozen");
        articles[_articleId].isFrozen = false;
        articles[_articleId].votesInTimeWindow = 0;
        articles[_articleId].lastVoteTimestamp = block.timestamp;
        emit ArticleUnfrozen(_articleId, block.timestamp);
    }

    function getArticleStatus(uint256 _articleId) external view returns (
        string memory status,
        uint256 upvotes,
        uint256 downvotes,
        bool isFrozen
    ) {
        Article storage article = articles[_articleId];
        
        uint256 totalVotes = article.upvotes + article.downvotes;
        
        string memory currentStatus;
        if (totalVotes < 5) {
            currentStatus = "disputed";
        } else if (article.upvotes == article.downvotes) {
            currentStatus = "pending";
        } else if (article.upvotes > article.downvotes) {
            currentStatus = "verified";
        } else {
            currentStatus = "disputed";
        }
        
        return (
            currentStatus,
            article.upvotes,
            article.downvotes,
            article.isFrozen
        );
    }

    function hasUserVoted(uint256 _articleId, address _user) external view returns (bool, bool) {
        Vote memory userVote = articles[_articleId].userVotes[_user];
        return (userVote.timestamp > 0, userVote.isUpvote);
    }
}

